mapper.update:
UPDATE map_elements
name=#{name}, longitude=#{longitude}, latitude=#{latitude}, element_location=ST_GeomFromText(#{geoStr}, 4326),
WHERE id=#{id}

mapper.select:
select  id, name, longitude, latitude,
ST_AsGeoJson(element_location) as element_location
FROM map_elements
where 1 = 1

    and name like #{name}


    and longitude = #{longitude}


    and latitude = #{latitude}
